            }
            return result;
        }
    }
}

contracts/DynamicPresale.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IMyToken is IERC20 {
    function mint(address to, uint256 amount) external;
}

/// @title DynamicPresale - Dynamic Token Presale Contract
/// @notice Handles multi-phase presale, buy, claim, refund, and withdraw logic
contract DynamicPresale is ReentrancyGuard, Pausable, Ownable {
    using EnumerableSet for EnumerableSet.AddressSet;
    using Address for address payable;

    struct Phase {
        uint256 priceWei; // price per token unit in wei
        uint256 supply;   // tokens available in this phase (token units)
        uint256 sold;     // tokens sold in this phase (token units)
        uint256 start;    // unix timestamp
        uint256 end;      // unix timestamp
    }

    IMyToken public immutable token;
    uint8 public immutable tokenDecimals;
    uint256 public immutable tokenUnit; // 10 ** tokenDecimals

    // configurable sale parameters (owner-settable)
    uint256 public softCap;      // wei
    uint256 public minBuy;       // wei
    uint256 public maxPerWallet; // wei

    Phase[] public phases;

    uint256 public totalRaised;     // wei accepted (costs accepted)
    uint256 public totalTokensSold; // token units sold
    bool public saleEnded;
    bool public softCapReached;

    mapping(address => uint256) public contributionsWei; // wei accepted per buyer
    mapping(address => uint256) public pendingTokens;    // token units pending to be claimed
    EnumerableSet.AddressSet private buyers;

    // local escrow (pull-payment) mapping: recipient => wei pending
    mapping(address => uint256) private _escrowPayments;

    // Sum of all escrowed wei pending (keeps invariant to protect funds)
    uint256 public totalEscrow;

    event Purchased(address indexed buyer, uint256 indexed phaseId, uint256 ethAmount, uint256 tokensAmount);
    event Claimed(address indexed buyer, uint256 tokensAmount);
    event RefundRequested(address indexed buyer, uint256 ethAmount);
    event PaymentQueued(address indexed dest, uint256 amount);
    event PhaseAdded(uint256 indexed phaseId, uint256 priceWei, uint256 supply, uint256 start, uint256 end);
    event SoftCapReached(uint256 totalRaised);
    event Withdrawn(address indexed beneficiary, uint256 amount);
    event SaleEnded(bool softCapReached);
    event PaymentsWithdrawn(address indexed payee, uint256 amount);

    modifier onlyWhileActive() {
        require(!saleEnded, "Presale: sale ended");
        _;
    }

    /// @notice Constructor sets token, decimals and initial sale params
    /// NOTE: Ownable requires an initial owner parameter in OZ v5, por eso pasamos msg.sender.
    constructor(
        address token_,
        uint8 tokenDecimals_,
        uint256 softCap_,
        uint256 minBuy_,
        uint256 maxPerWallet_
    ) Ownable(msg.sender) {
        require(token_ != address(0), "Presale: token address zero");
        require(softCap_ > 0, "Presale: softCap must be greater than 0");
        require(minBuy_ > 0, "Presale: minBuy must be greater than 0");
        require(maxPerWallet_ >= minBuy_, "Presale: maxPerWallet must be >= minBuy");

        token = IMyToken(token_);
        tokenDecimals = tokenDecimals_;
        tokenUnit = 10 ** uint256(tokenDecimals_);
        softCap = softCap_;
        minBuy = minBuy_;
        maxPerWallet = maxPerWallet_;
    }
    // -------------------------
    // Pull-payment (local)
    // -------------------------

    /// @notice Internal helper to queue a payment for `dest` (pull pattern)
    function _asyncTransfer(address dest, uint256 amount) internal {
        require(dest != address(0), "Presale: dest zero");
        require(amount > 0, "Presale: zero amount");
        _escrowPayments[dest] += amount;
        totalEscrow += amount;
        emit PaymentQueued(dest, amount);
    }

    /// @notice Withdraw pending payments (excess/refund). Uses pull-pattern.
    function withdrawPayments() external nonReentrant {
        uint256 payment = _escrowPayments[msg.sender];
        require(payment > 0, "Presale: no payments");

        // Effects (CEI)
        _escrowPayments[msg.sender] = 0;
        if (totalEscrow >= payment) {
            totalEscrow -= payment;
        } else {
            totalEscrow = 0;
        }

        // Use Address.sendValue to forward all gas and be compatible with smart contract wallets
        Address.sendValue(payable(msg.sender), payment);
        emit PaymentsWithdrawn(msg.sender, payment);
    }

    /// @notice View pending payments for an account
    function paymentsOf(address account) external view returns (uint256) {
        return _escrowPayments[account];
    }

    /// @notice Returns the total escrowed ETH reserved for buyer withdrawals
    function escrowBalance() external view returns (uint256) {
        return totalEscrow;
    }

    // -------------------------
    // Phase management
    // -------------------------

    /// @notice Add a new phase. Only owner can add.
    function addPhase(uint256 priceWei, uint256 supply, uint256 start, uint256 end) external onlyOwner {
        require(priceWei > 0, "Presale: price must be greater than 0");
        require(supply > 0, "Presale: supply must be greater than 0");
        require(start < end, "Presale: invalid phase time");
        require(start > block.timestamp, "Presale: start time must be in future");

        // Disallow overlapping phases
        for (uint256 i = 0; i < phases.length; i++) {
            require(start >= phases[i].end || end <= phases[i].start, "Presale: overlapping phases");
        }

        phases.push(Phase({ priceWei: priceWei, supply: supply, sold: 0, start: start, end: end }));
        emit PhaseAdded(phases.length - 1, priceWei, supply, start, end);
    }

    /// @notice Update phase times for a future (not-yet-started) phase
    function updatePhase(uint256 phaseId, uint256 newStart, uint256 newEnd) external onlyOwner {
        require(phaseId < phases.length, "Presale: invalid phase ID");
        require(newStart < newEnd, "Presale: invalid phase time");
        Phase storage phase = phases[phaseId];
        require(block.timestamp < phase.start, "Presale: cannot update active/past phase");

        // Ensure no overlap with other phases
        for (uint256 i = 0; i < phases.length; i++) {
            if (i == phaseId) continue;
            require(newStart >= phases[i].end || newEnd <= phases[i].start, "Presale: overlapping phases");
        }

        phase.start = newStart;
        phase.end = newEnd;
    }

    /// @notice Pause a specific phase by setting its end time to now (emergency)
    function pausePhase(uint256 phaseId) external onlyOwner {
        require(phaseId < phases.length, "Presale: invalid phase ID");
        phases[phaseId].end = block.timestamp;
    }

    // -------------------------
    // Buy / Claim / Refund
    // -------------------------

    /// @notice Internal: find active phase index if any (non-reverting)
    function _currentPhaseIndex() internal view returns (bool, uint256) {
        for (uint256 i = 0; i < phases.length; i++) {
            Phase storage p = phases[i];
            bool timeOk = (p.start == 0 || block.timestamp >= p.start) && (p.end == 0 || block.timestamp <= p.end);
            bool hasSupply = p.sold < p.supply;
            if (timeOk && hasSupply) return (true, i);
        }
        return (false, 0);
    }

    /// @notice Buy tokens in current phase. Excess ETH will be queued for withdrawal via `withdrawPayments`.
    function buy() external payable nonReentrant whenNotPaused onlyWhileActive {
        require(msg.value >= minBuy, "Presale: below min buy");

        (bool found, uint256 phaseId) = _currentPhaseIndex();
        require(found, "Presale: no active phase");

        Phase storage phase = phases[phaseId];

        // Calculate tokens buyer can afford (in token units)
        uint256 tokensToBuy = (msg.value * tokenUnit) / phase.priceWei;
        require(tokensToBuy > 0, "Presale: zero tokens");

        uint256 available = phase.supply - phase.sold;
        uint256 tokensAllocated = tokensToBuy > available ? available : tokensToBuy;

        uint256 cost = (tokensAllocated * phase.priceWei) / tokenUnit;
        require(contributionsWei[msg.sender] + cost <= maxPerWallet, "Presale: above max per wallet");

        uint256 excess = msg.value - cost;

        // Effects (CEI)
        phase.sold += tokensAllocated;
        totalRaised += cost;
        totalTokensSold += tokensAllocated;
        contributionsWei[msg.sender] += cost;
        pendingTokens[msg.sender] += tokensAllocated;
        buyers.add(msg.sender);

        // Handle excess via pull (escrow)
        if (excess > 0) {
            _asyncTransfer(msg.sender, excess);
        }

        emit Purchased(msg.sender, phaseId, cost, tokensAllocated);

        if (!softCapReached && totalRaised >= softCap) {
            softCapReached = true;
            emit SoftCapReached(totalRaised);
        }
    }

    /// @notice Claim minted tokens after sale ended and soft cap reached
    function claim() external nonReentrant whenNotPaused {
        require(saleEnded, "Presale: sale not ended");
        require(softCapReached, "Presale: softCap not reached");

        uint256 amount = pendingTokens[msg.sender];
        require(amount > 0, "Presale: nothing to claim");

        pendingTokens[msg.sender] = 0;
        token.mint(msg.sender, amount);
        emit Claimed(msg.sender, amount);
    }

    /// @notice Request refund when sale ended and soft cap not reached
    function requestRefund() external nonReentrant whenNotPaused {
        require(saleEnded, "Presale: sale not ended");
        require(!softCapReached, "Presale: softCap reached");

        uint256 contributed = contributionsWei[msg.sender];
        require(contributed > 0, "Presale: nothing to refund");

        contributionsWei[msg.sender] = 0;
        pendingTokens[msg.sender] = 0;

        // Queue refund via pull pattern
        _asyncTransfer(msg.sender, contributed);
        emit RefundRequested(msg.sender, contributed);
    }

    // -------------------------
    // Admin actions
    // -------------------------

    /// @notice Withdraw proceeds (ETH held in contract) to beneficiary. Only owner and only when softCapReached and saleEnded.
    function withdrawProceeds(address payable beneficiary) external onlyOwner nonReentrant {
        require(saleEnded, "Presale: sale not ended");
        require(softCapReached, "Presale: softCap not reached");

        uint256 currentBalance = address(this).balance;
        // Do not withdraw funds that are reserved for escrow/refunds
        require(currentBalance > totalEscrow, "Presale: nothing withdrawable (reserved escrow)");
        uint256 withdrawable = currentBalance - totalEscrow;
        Address.sendValue(beneficiary, withdrawable);
        emit Withdrawn(beneficiary, withdrawable);
    }

    /// @notice End the sale. Only owner.
    function endSale() external onlyOwner {
        require(!saleEnded, "Presale: already ended");
        saleEnded = true;
        emit SaleEnded(softCapReached);
    }

    /// @notice Pause the presale (global). Only owner.
    function pause() external onlyOwner {
        _pause();
    }

    /// @notice Unpause the presale (global). Only owner.
    function unpause() external onlyOwner {
        _unpause();
    }

    // -------------------------
    // View / helpers
    // -------------------------

    /// @notice Reverting current phase getter (keeps backward compatibility)
    function getCurrentPhase() public view returns (uint256) {
        for (uint256 i = 0; i < phases.length; i++) {
            if (block.timestamp >= phases[i].start && block.timestamp <= phases[i].end) {
                return i;
            }
        }
        revert("Presale: no active phase");
    }

    /// @notice Returns whether there is an active phase
    function hasActivePhase() external view returns (bool) {
        (bool found, ) = _currentPhaseIndex();
        return found;
    }

    /// @notice Get phase details
    function getPhase(uint256 phaseId) external view returns (Phase memory) {
        require(phaseId < phases.length, "Presale: invalid phase ID");
        return phases[phaseId];
    }

    /// @notice Total number of configured phases
    function totalPhases() external view returns (uint256) {
        return phases.length;
    }

    /// @notice Calculate tokens + cost + excess if buying `ethAmount` in current phase (safe, non-reverting)
    function calculateTokens(uint256 ethAmount) external view returns (uint256 tokens, uint256 cost, uint256 excess) {
        (bool found, uint256 phaseId) = _currentPhaseIndex();
        if (!found) return (0, 0, ethAmount);
        Phase memory phase = phases[phaseId];

        uint256 tokensToBuy = (ethAmount * tokenUnit) / phase.priceWei;
        uint256 available = phase.supply - phase.sold;
        uint256 tokensAllocated = tokensToBuy > available ? available : tokensToBuy;

        cost = (tokensAllocated * phase.priceWei) / tokenUnit;
        excess = ethAmount - cost;
        return (tokensAllocated, cost, excess);
    }

    /// @notice Remaining tokens in current phase (0 if none)
    function remainingTokensInCurrentPhase() external view returns (uint256) {
        (bool found, uint256 idx) = _currentPhaseIndex();
        if (!found) return 0;
        return phases[idx].supply - phases[idx].sold;
    }

    /// @notice Number of buyers (addresses) who participated
    function totalBuyers() external view returns (uint256) {
        return buyers.length();
    }

    // -------------------------
    // Admin setters (optional, owner-only)
    // -------------------------

    /// @notice Set soft cap (only until reached)
    function setSoftCap(uint256 newSoftCap) external onlyOwner {
        require(newSoftCap > 0, "Presale: softCap > 0");
        require(!softCapReached, "Presale: softCap already reached");
        softCap = newSoftCap;
    }

    /// @notice Set min buy
    function setMinBuy(uint256 newMinBuy) external onlyOwner {
        require(newMinBuy > 0, "Presale: minBuy > 0");
        require(newMinBuy <= maxPerWallet, "Presale: minBuy <= maxPerWallet");
        minBuy = newMinBuy;
    }

    /// @notice Set max per wallet
    function setMaxPerWallet(uint256 newMaxPerWallet) external onlyOwner {
        require(newMaxPerWallet >= minBuy, "Presale: maxPerWallet >= minBuy");
        maxPerWallet = newMaxPerWallet;
    }

    // Allow contract to receive ETH (for example direct transfers)
    receive() external payable {}
}

contracts/MyToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Capped.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/// @title MyToken - Professional ERC20 Token for Dynamic Presale
/// @notice Implements minting, burning, pausing, capping and role-based access control
/// @dev Uses AccessControl for granular permissions, supports capped supply (cap must be > 0)
contract MyToken is ERC20, ERC20Burnable, ERC20Capped, Pausable, AccessControl {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");

    /// @notice Emitted when admin performs an emergency burn from an account
    event EmergencyBurn(address indexed from, uint256 amount, address indexed admin);

    /// @notice Constructor sets name, symbol, cap and initial admin
    /// @param name_ Token name
    /// @param symbol_ Token symbol
    /// @param cap_ Maximum supply cap (must be > 0). If you need an unlimited supply, do not inherit ERC20Capped.
    constructor(
        string memory name_,
        string memory symbol_,
        uint256 cap_
    ) ERC20(name_, symbol_) ERC20Capped(cap_) {
        // ERC20Capped's constructor already requires cap_ > 0 (OpenZeppelin).
        // Keep logic explicit.
        require(cap_ > 0, "MyToken: cap is 0");

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        _grantRole(PAUSER_ROLE, msg.sender);
        _grantRole(BURNER_ROLE, msg.sender);
    }

    /// @notice Mint new tokens to an address. Only accounts with MINTER_ROLE can mint.
    /// @param to Address to mint tokens to
    /// @param amount Amount of tokens to mint (in base units)
    function mint(
        address to,
        uint256 amount
    ) external onlyRole(MINTER_ROLE) whenNotPaused {
        require(to != address(0), "MyToken: mint to zero address");
        require(amount > 0, "MyToken: mint amount must be > 0");
        _mint(to, amount);
    }

    /// @notice Pause all token transfers and minting. Only accounts with PAUSER_ROLE can pause.
    function pause() external onlyRole(PAUSER_ROLE) {
        _pause();
    }

    /// @notice Unpause token transfers and minting. Only accounts with PAUSER_ROLE can unpause.
    function unpause() external onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    /// @notice Burn tokens from caller (inherited from ERC20Burnable).
    /// @dev keep default ERC20Burnable.burn semantics

    /// @notice Allow addresses with BURNER_ROLE to burn tokens from an address without allowance checks.
    /// @dev This is an admin-style burn for operators with the role.
    function burnFromByRole(address from, uint256 amount) external onlyRole(BURNER_ROLE) {
        require(from != address(0), "MyToken: burn from zero address");
        _burn(from, amount);
    }

    /// @notice Emergency burn function for admin. Only DEFAULT_ADMIN_ROLE.
    /// @param from Address to burn from
    /// @param amount Amount to burn
    function emergencyBurn(
        address from,
        uint256 amount
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(from != address(0), "MyToken: burn from zero address");
        _burn(from, amount);
        emit EmergencyBurn(from, amount, msg.sender);
    }

    /// @dev Override _update to respect pause state (OpenZeppelin v5)
    function _update(
        address from,
        address to,
        uint256 value
    ) internal override(ERC20, ERC20Capped) whenNotPaused {
        super._update(from, to, value);
    }

    /// @dev Required override for AccessControl (ERC165)
    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}

contracts/TokenVesting.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/// @title TokenVesting - Professional linear vesting contract for Dynamic Presale
/// @notice Allows beneficiaries to release vested tokens according to schedule with cliff period
/// @dev Supports multiple schedules per beneficiary, revocable vesting, and emergency functions
contract TokenVesting is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    struct VestingSchedule {
        uint256 totalAmount; // total tokens allocated to schedule
        uint256 released; // tokens already released
        uint256 start; // start timestamp
        uint256 duration; // total duration in seconds
        uint256 cliff; // cliff in seconds from start
        bool revocable; // can owner revoke?
        bool revoked; // already revoked?
    }

    IERC20 public immutable token;

    // Counters / bookkeeping
    uint256 public totalVestingSchedules;
    uint256 public totalCommitted; // tokens reserved (not yet distributed) across all schedules
    mapping(address => VestingSchedule[]) private schedules;
    mapping(address => uint256) public totalVestedAmount; // total remaining committed tokens per beneficiary (not yet released)

    event VestingCreated(
        address indexed beneficiary,
        uint256 indexed scheduleId,
        uint256 totalAmount,
        uint256 start,
        uint256 duration,
        uint256 cliff,
        bool revocable
    );
    event TokensReleased(
        address indexed beneficiary,
        uint256 indexed scheduleId,
        uint256 amount
    );
    event VestingRevoked(
        address indexed beneficiary,
        uint256 indexed scheduleId,
        uint256 unvestedAmount
    );

    /// @notice Constructor
    /// @param token_ ERC20 token used for vesting
    constructor(address token_) Ownable(msg.sender) {
        require(token_ != address(0), "Vesting: token address zero");
        token = IERC20(token_);
    }

    // -------------------------
    // Create / Manage vesting
    // -------------------------

    /// @notice Create vesting schedule for beneficiary. Only owner.
    /// @param beneficiary Address of the beneficiary
    /// @param totalAmount Total amount of tokens to vest (in token units)
    /// @param start Start timestamp of vesting (>= now)
    /// @param duration Duration of vesting in seconds (> 0)
    /// @param cliff Cliff period in seconds (<= duration)
    /// @param revocable Whether the vesting can be revoked by owner
    function createVesting(
        address beneficiary,
        uint256 totalAmount,
        uint256 start,
        uint256 duration,
        uint256 cliff,
        bool revocable
    ) external onlyOwner whenNotPaused {
        require(beneficiary != address(0), "Vesting: beneficiary zero");
        require(totalAmount > 0, "Vesting: zero amount");
        require(duration > 0, "Vesting: zero duration");
        require(cliff <= duration, "Vesting: cliff greater than duration");
        require(start >= block.timestamp, "Vesting: start time in past");

        // Ensure contract has enough tokens for this new vesting (considering previously committed)
        require(
            token.balanceOf(address(this)) >= totalCommitted + totalAmount,
            "Vesting: insufficient token balance for new vesting"
        );

        schedules[beneficiary].push(
            VestingSchedule({
                totalAmount: totalAmount,
                released: 0,
                start: start,
                duration: duration,
                cliff: cliff,
                revocable: revocable,
                revoked: false
            })
        );

        totalVestedAmount[beneficiary] += totalAmount;
        totalCommitted += totalAmount;
        totalVestingSchedules++;

        uint256 scheduleId = schedules[beneficiary].length - 1;
        emit VestingCreated(
            beneficiary,
            scheduleId,
            totalAmount,
            start,
            duration,
            cliff,
            revocable
        );
    }

    // -------------------------
    // Release functions
    // -------------------------

    /// @notice Release vested tokens for all schedules of the caller
    function release() external nonReentrant whenNotPaused {
        uint256 totalReleasable = 0;
        VestingSchedule[] storage userSchedules = schedules[msg.sender];

        for (uint256 i = 0; i < userSchedules.length; i++) {
            VestingSchedule storage schedule = userSchedules[i];
            if (schedule.revoked) continue;

            uint256 vested = _vestedAmount(schedule);
            uint256 unreleased = 0;
            if (vested > schedule.released) {
                unreleased = vested - schedule.released;
            } else {
                continue;
            }

            // Update schedule and global counters BEFORE transfer (CEI)
            schedule.released += unreleased;
            totalReleasable += unreleased;

            // bookkeeping: reduce committed and per-beneficiary committed
            // totalCommitted represented tokens not yet distributed, so subtract released now
            if (totalCommitted >= unreleased) {
                totalCommitted -= unreleased;
            } else {
                totalCommitted = 0;
            }

            if (totalVestedAmount[msg.sender] >= unreleased) {
                totalVestedAmount[msg.sender] -= unreleased;
            } else {
                totalVestedAmount[msg.sender] = 0;
            }

            emit TokensReleased(msg.sender, i, unreleased);
        }

        require(totalReleasable > 0, "Vesting: nothing to release");
        token.safeTransfer(msg.sender, totalReleasable);
    }

    /// @notice Release vested tokens for a specific schedule
    /// @param scheduleId Index of the schedule to release
    function releaseSchedule(
        uint256 scheduleId
    ) external nonReentrant whenNotPaused {
        VestingSchedule[] storage userSchedules = schedules[msg.sender];
        require(
            scheduleId < userSchedules.length,
            "Vesting: invalid schedule ID"
        );

        VestingSchedule storage schedule = userSchedules[scheduleId];
        require(!schedule.revoked, "Vesting: schedule revoked");

        uint256 vested = _vestedAmount(schedule);
        require(vested > schedule.released, "Vesting: nothing to release");
        uint256 unreleased = vested - schedule.released;

        // Update accounting (CEI)
        schedule.released += unreleased;

        if (totalCommitted >= unreleased) {
            totalCommitted -= unreleased;
        } else {
            totalCommitted = 0;
        }

        if (totalVestedAmount[msg.sender] >= unreleased) {
            totalVestedAmount[msg.sender] -= unreleased;
        } else {
            totalVestedAmount[msg.sender] = 0;
        }

        token.safeTransfer(msg.sender, unreleased);
        emit TokensReleased(msg.sender, scheduleId, unreleased);
    }

    // -------------------------
    // Vesting math
    // -------------------------

    /// @notice Calculate vested amount for a schedule with cliff support
    function _vestedAmount(
        VestingSchedule memory schedule
    ) internal view returns (uint256) {
        if (block.timestamp < schedule.start + schedule.cliff) {
            return 0;
        } else if (block.timestamp >= schedule.start + schedule.duration) {
            return schedule.totalAmount;
        } else {
            uint256 elapsed = block.timestamp - schedule.start;
            // Compute safely: avoid large multiplication before division
            uint256 part1 = schedule.totalAmount / schedule.duration;
            uint256 part2 = schedule.totalAmount % schedule.duration;
            return part1 * elapsed + (part2 * elapsed) / schedule.duration;
        }
    }

    // -------------------------
    // Revocation
    // -------------------------

    /// @notice Revoke vesting schedule. Only owner and only if revocable.
    /// @param beneficiary Address of beneficiary
    /// @param scheduleId Index of schedule to revoke
    function revokeVesting(
        address beneficiary,
        uint256 scheduleId
    ) external onlyOwner {
        VestingSchedule[] storage userSchedules = schedules[beneficiary];
        require(
            scheduleId < userSchedules.length,
            "Vesting: invalid schedule ID"
        );

        VestingSchedule storage schedule = userSchedules[scheduleId];
        require(schedule.revocable, "Vesting: not revocable");
        require(!schedule.revoked, "Vesting: already revoked");

        uint256 vested = _vestedAmount(schedule);
        uint256 unvested = 0;
        if (schedule.totalAmount > vested) {
            unvested = schedule.totalAmount - vested;
        } // else 0

        schedule.revoked = true;

        // Reduce total committed by the unvested portion (released portion already reduced on release)
        if (totalCommitted >= unvested) {
            totalCommitted -= unvested;
        } else {
            totalCommitted = 0;
        }

        // Reduce per-beneficiary committed by unvested
        if (totalVestedAmount[beneficiary] >= unvested) {
            totalVestedAmount[beneficiary] -= unvested;
        } else {
            totalVestedAmount[beneficiary] = 0;
        }

        // Return the unvested tokens to owner (admin)
        if (unvested > 0) {
            token.safeTransfer(owner(), unvested);
        }

        emit VestingRevoked(beneficiary, scheduleId, unvested);
    }

    // -------------------------
    // Admin utilities
    // -------------------------

    /// @notice Emergency withdraw tokens. Only owner.
    /// @param amount Amount to withdraw
    function emergencyWithdraw(uint256 amount) external onlyOwner {
        require(
            amount <= token.balanceOf(address(this)),
            "Vesting: insufficient balance"
        );
        token.safeTransfer(owner(), amount);
    }

    /// @notice Pause vesting operations. Only owner.
    function pause() external onlyOwner {
        _pause();
    }

    /// @notice Unpause vesting operations. Only owner.
    function unpause() external onlyOwner {
        _unpause();
    }

    // -------------------------
    // Views / helpers
    // -------------------------

    /// @notice Get number of schedules for a beneficiary
    /// @param beneficiary Address to check
    function getScheduleCount(
        address beneficiary
    ) external view returns (uint256) {
        return schedules[beneficiary].length;
    }

    /// @notice Get vested amount for a specific schedule
    /// @param beneficiary Address of beneficiary
    /// @param scheduleId Index of schedule
    function getVestedAmount(
        address beneficiary,
        uint256 scheduleId
    ) external view returns (uint256) {
        require(
            scheduleId < schedules[beneficiary].length,
            "Vesting: invalid schedule ID"
        );
        return _vestedAmount(schedules[beneficiary][scheduleId]);
    }

    /// @notice Get releasable amount for a specific schedule
    /// @param beneficiary Address of beneficiary
    /// @param scheduleId Index of schedule
    function getReleasableAmount(
        address beneficiary,
        uint256 scheduleId
    ) external view returns (uint256) {
        require(
            scheduleId < schedules[beneficiary].length,
            "Vesting: invalid schedule ID"
        );
        VestingSchedule memory schedule = schedules[beneficiary][scheduleId];
        if (schedule.revoked) return 0;
        uint256 vested = _vestedAmount(schedule);
        return (vested > schedule.released) ? (vested - schedule.released) : 0;
    }

    /// @notice Get total releasable amount for all schedules of a beneficiary
    /// @param beneficiary Address to check
    function getTotalReleasableAmount(
        address beneficiary
    ) external view returns (uint256) {
        uint256 totalReleasable = 0;
        VestingSchedule[] memory userSchedules = schedules[beneficiary];

        for (uint256 i = 0; i < userSchedules.length; i++) {
            if (userSchedules[i].revoked) continue;
            uint256 vested = _vestedAmount(userSchedules[i]);
            uint256 unreleased = (vested > userSchedules[i].released)
                ? (vested - userSchedules[i].released)
                : 0;
            totalReleasable += unreleased;
        }

        return totalReleasable;
    }

    /// @notice Get schedule details
    /// @param beneficiary Address of beneficiary
    /// @param scheduleId Index of schedule
    function getSchedule(
        address beneficiary,
        uint256 scheduleId
    ) external view returns (VestingSchedule memory) {
        require(
            scheduleId < schedules[beneficiary].length,
            "Vesting: invalid schedule ID"
        );
        return schedules[beneficiary][scheduleId];
    }

    /// @notice Get total committed tokens across all vesting schedules (not yet distributed)
    function getTotalCommitted() external view returns (uint256) {
        return totalCommitted;
    }
}
